mapper.select:
select

from SPORT_AGAINST_INFO
where SYSTEM_CODE=#{systemCode} and LOTTERY_CODE=#{lotteryCode}

	select

	from SPORT_AGAINST_INFO
	where LOTTERY_CODE = #{lotteryCode}

		and ISSUE_CODE IN

			#{issueCode}



		and SYSTEM_CODE IN

			#{systemCode}



        and OFFICIAL_MATCH_CODE IN 

			#{officialMatchCode}


	order by SALE_END_TIME asc

select ls.*,LOGO_URL as visitiLogoUrl ,TEAM_SHORT_NAME as visitiShortName from ( SELECT A.issue_code issueCode,A.OFFICIAL_MATCH_CODE as officialMatchCode , A.SYSTEM_CODE as systemCode, A. ID,A .LOTTERY_CODE as lotteryCode,SALE_END_TIME as saleEndTime, MATCH_NAME as matchName,START_TIME as startTime,HOME_NAME as homeName,VISITI_NAME as visitiName, NEWEST_SP_WIN as newestSpWin,NEWEST_SP_DRAW as newestSpDraw,NEWEST_SP_FAIL as newestSpFail,LOGO_URL as homeLogoUrl,TEAM_SHORT_NAME as homeShortName ,VISITI_TEAM_ID FROM SPORT_AGAINST_INFO A JOIN SPORT_DATA_FB_SP B ON B.SPORT_AGAINST_INFO_ID = A . ID left join sport_team_info T1 on HOME_TEAM_ID = T1.id WHERE TEAM_TYPE = 1 AND LOTTERY_CODE = #{lotteryCode} AND IS_RECOMMEND = 1 AND MATCH_STATUS = 9 AND SALE_END_TIME >now()
AND date_format(SALE_END_TIME,'%Y-%m-%d') = #{queryDate,jdbcType=TIMESTAMP}
) ls left join sport_team_info T2 on VISITI_TEAM_ID = T2.id where TEAM_TYPE = 1

select ls.*,LOGO_URL as visitiLogoUrl ,TEAM_SHORT_NAME as visitiShortName from ( SELECT A.issue_code issueCode, A.OFFICIAL_MATCH_CODE AS officialMatchCode, A.SYSTEM_CODE AS systemCode, A.ID, A.LOTTERY_CODE AS lotteryCode, A.SALE_END_TIME AS saleEndTime, A.MATCH_NAME AS matchName, A.START_TIME AS startTime, A.HOME_NAME AS homeName, A.VISITI_NAME AS visitiName, B.NEWEST_SP_WIN AS newestSpWin, B.NEWEST_SP_FAIL AS newestSpFail, T1.LOGO_URL AS homeLogoUrl, T1.TEAM_SHORT_NAME AS homeShortName, VISITI_TEAM_ID FROM SPORT_AGAINST_INFO A JOIN SPORT_DATA_BB_SP B ON B.SPORT_AGAINST_INFO_ID = A.ID LEFT JOIN sport_team_info T1 ON HOME_TEAM_ID = T1.id LEFT JOIN sport_match_info SI on A.sport_match_info_id = SI.id WHERE TEAM_TYPE = 2 and match_type =2 AND LOTTERY_CODE = #{lotteryCode} AND IS_RECOMMEND = 1 AND MATCH_STATUS = 9 AND SALE_END_TIME >now()
AND date_format(SALE_END_TIME,'%Y-%m-%d') = #{queryDate,jdbcType=TIMESTAMP}
) ls left join sport_team_info T2 on VISITI_TEAM_ID = T2.id where TEAM_TYPE = 2

mapper.sql:
select t.id from ((SELECT sum(NEWEST_SP_WIN+NEWEST_LET_SP_FAIL) as totalNum ,A.id FROM SPORT_AGAINST_INFO A JOIN SPORT_DATA_FB_SP B ON B.SPORT_AGAINST_INFO_ID = A . ID WHERE LOTTERY_CODE = #{lotteryCode} AND MATCH_STATUS = 9 AND start_time =#{date} AND NEWEST_LET_NUM =-1
and SYSTEM_CODE != #{systemCode}
group by A.id) union ( SELECT sum(NEWEST_SP_FAIL+NEWEST_LET_SP_WIN) as totalNum ,A.id FROM SPORT_AGAINST_INFO A JOIN SPORT_DATA_FB_SP B ON B.SPORT_AGAINST_INFO_ID = A . ID WHERE LOTTERY_CODE = #{lotteryCode} AND MATCH_STATUS = 9 AND start_time =#{date} AND NEWEST_LET_NUM =1
and SYSTEM_CODE != #{systemCode}
group by A.id) ) t order by t.totalNum desc limit 1

mapper.select:
select start_time startTime from ((SELECT start_time ,A.id FROM SPORT_AGAINST_INFO A JOIN SPORT_DATA_FB_SP B ON B.SPORT_AGAINST_INFO_ID = A . ID WHERE LOTTERY_CODE = #{lotteryCode} AND A.visiti_team_id is not null AND MATCH_STATUS = 9 AND start_time >=#{date} AND (NEWEST_LET_NUM =-1 or NEWEST_LET_NUM =1)
and SYSTEM_CODE != #{systemCode}
group by A.id) ) t order by start_time limit 1

select

from SPORT_AGAINST_INFO t
where t.lottery_code=#{lotteryCode}
and t.SYSTEM_CODE = #{systemCode}

	and t.issue_code =#{lotteryIssue}

limit 1
