mapper.select:
SELECT B.ID as SPORT_AGAINST_INFO_ID, B.LOTTERY_CODE, B.ISSUE_CODE, B.SYSTEM_CODE, B.HOME_TEAM_ID, B.VISITI_TEAM_ID, B.START_TIME, B.SPORT_MATCH_INFO_ID, B.MATCH_NAME, B.HOME_NAME, B.VISITI_NAME, B.MATCH_STATUS, B.MATCH_ANALYSIS_URL, B.MATCH_INFO_URL, B.IS_RECOMMEND, B.MATCH_LABEL_COLOR, B.OFFICIAL_MATCH_CODE, B.OFFICIAL_ID, B.IS_NEUTRAL, B.STADIUM, B.WEATHER, B.SALE_END_TIME, B.ODDS_WIN, B.ODDS_DRAW, B.ODDS_FAIL, A.ID as SPORT_DRAW_WF_ID, A.FULL_SCORE, A.LET_SF, A.SP_LET_WF, A.DRAW_TIME, C.let_score LET_NUM, E.LOTTERY_TIME, E.SALE_STATUS, M.MATCH_ID, M.MATCH_DATA_URL, M.MATCH_FULL_NAME, M.MATCH_SHORT_NAME, M.LOGO_URL, M.MATCH_TYPE, H.ID as homeTeamPrimaryId, H.TEAM_DATA_URL AS HOME_TEAM_DATA_URL, H.TEAM_FULL_NAME AS HOME_TEAM_FULL_NAME, H.TEAM_SHORT_NAME AS HOME_TEAM_SHORT_NAME, H.TEAM_ORDER AS HOME_TEAM_ORDER, H.LOGO_URL AS HOME_LOGO_URL, G.id as guestTeamPrimaryId, G.TEAM_DATA_URL AS GUEST_TEAM_DATA_URL, G.TEAM_FULL_NAME AS GUEST_TEAM_FULL_NAME, G.TEAM_SHORT_NAME AS GUEST_TEAM_SHORT_NAME, G.TEAM_ORDER AS GUEST_TEAM_ORDER, G.LOGO_URL AS GUEST_LOGO_URL, I.ID as SPORT_STATUS_WF_ID, I.STATUS_LET_WF, B.CREATE_BY, B.MODIFY_BY, B.MODIFY_TIME, B.UPDATE_TIME, B.REMARK
ORDER BY
${sortField} ${sortOrder}
B.MATCH_STATUS DESC, B.ISSUE_CODE,B.BJ_NUM
LIMIT #{startRow,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.insert:
insert into SPORT_DRAW_WF (SPORT_AGAINST_INFO_ID,
  FULL_SCORE, LET_NUM, LET_SF, 
  SP_LET_WF, DRAW_TIME, MODIFY_BY, 
  MODIFY_TIME, UPDATE_TIME, CREATE_TIME
  )
values (#{sportAgainstInfoId,jdbcType=DECIMAL},
  #{fullScore,jdbcType=VARCHAR}, #{letNum,jdbcType=DECIMAL}, #{letSf,jdbcType=DECIMAL}, 
  #{spLetWf,jdbcType=FLOAT}, #{drawTime,jdbcType=TIMESTAMP}, #{modifyBy,jdbcType=VARCHAR},
  #{modifyTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}
  )
