mapper.select:
			select li.issue_code
  				from lottery_issue li
 				where li.lottery_code = #{lotteryCode}
   				and li.issue_code >= (
   				           select issue_code
                           from lottery_issue
                           where lottery_code = #{lotteryCode}
                           and current_issue = 1)
                 order by li.issue_code  
                 LIMIT #{qryCount}

select li.ID id, li.ISSUE_CODE issueCode, li.LOTTERY_CODE lotteryCode, li.LOTTERY_NAME lotteryName, li.CURRENT_ISSUE currentIssue, li.SALE_STATUS saleStatus, li.SALE_TIME saleTime, li.SALE_END_TIME saleEndTime, li.LOTTERY_TIME lotteryTime, li.DRAW_CODE drawCode, li.UPDATE_TIME updateTime, li.CREATE_TIME createTime from lottery_issue li left join lottery_type lt on lt.lottery_code = li.lottery_code
and li.LOTTERY_CODE = #{lotteryCode}
and li.ISSUE_CODE = #{issueCode}
and li.SALE_STATUS = #{saleStatus}
and lt.LOTTERY_CATEGORY = #{lotteryCategory}
order by
${sortField} ${sortOrder}
li.id desc
limit #{startRow},#{pageSize}

	select 
       count(1)
    from 
         lottery_issue li
    left join  lottery_type lt on lt.lottery_code = li.lottery_code 


          and li.LOTTERY_CODE = #{lotteryCode}


          and li.ISSUE_CODE =  #{issueCode}


          and li.SALE_STATUS =  #{saleStatus}


          and lt.LOTTERY_CATEGORY = #{lotteryCategory}

select @rownum:=@rownum+1 AS id, li.ISSUE_CODE issueCode, li.LOTTERY_NAME lotteryName, li.CURRENT_ISSUE currentIssue, li.SALE_STATUS saleStatus, date_format( li.SALE_TIME,'%Y-%m-%d %H:%i:%s') saleTime, date_format(li.SALE_END_TIME,'%Y-%m-%d %H:%i:%s') saleEndTime, date_format(li.LOTTERY_TIME,'%Y-%m-%d %H:%i:%s') lotteryTime, li.DRAW_CODE drawCode, date_format(li.UPDATE_TIME,'%Y-%m-%d %H:%i:%s') updateTime, date_format(li.CREATE_TIME,'%Y-%m-%d %H:%i:%s') createTime from lottery_issue li left join lottery_type lt on lt.lottery_code = li.lottery_code, (SELECT @rownum:=0) r
and li.LOTTERY_CODE = #{lotteryCode}
and li.ISSUE_CODE = #{issueCode}
and li.SALE_STATUS = #{saleStatus}
and lt.LOTTERY_CATEGORY = #{lotteryCategory}
order by li.ID

